INSERT INTO `wp_wc_admin_notes` VALUES("3", "new_in_app_marketplace_2021", "info", "en_US", "Customize your store with extensions", "Check out our NEW Extensions tab to see our favorite extensions for customizing your store, and discover the most popular extensions in the WooCommerce Marketplace.", "{}", "unactioned", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "1", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("4", "wayflyer_bnpl_q4_2021", "marketing", "en_US", "Grow your business with funding through Wayflyer", "Fast, flexible financing to boost cash flow and help your business grow – one fee, no interest rates, penalties, equity, or personal guarantees. Based on your store’s performance, Wayflyer provides funding and analytical insights to invest in your business.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("5", "wc_shipping_mobile_app_usps_q4_2021", "marketing", "en_US", "Print and manage your shipping labels with WooCommerce Shipping and the WooCommerce Mobile App", "Save time by printing, purchasing, refunding, and tracking shipping labels generated by <a href=\"https://woocommerce.com/woocommerce-shipping/\">WooCommerce Shipping</a> – all directly from your mobile device!", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("6", "woocommerce-services", "info", "en_US", "WooCommerce Shipping & Tax", "WooCommerce Shipping &amp; Tax helps get your store \"ready to sell\" as quickly as possible. You create your products. We take care of tax calculation, payment processing, and shipping label printing! Learn more about the extension that you just installed.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("7", "your-first-product", "info", "en_US", "Your first product", "That’s huge! You’re well on your way to building a successful online store — now it’s time to think about how you’ll fulfill your orders.<br /><br />Read our shipping guide to learn best practices and options for putting together your shipping strategy. And for WooCommerce stores in the United States, you can print discounted shipping labels via USPS with <a href=\"https://href.li/?https://woocommerce.com/shipping\" target=\"_blank\">WooCommerce Shipping</a>.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("8", "wc-admin-optimizing-the-checkout-flow", "info", "en_US", "Optimizing the checkout flow", "It’s crucial to get your store’s checkout as smooth as possible to avoid losing sales. Let’s take a look at how you can optimize the checkout experience for your shoppers.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("9", "wc-payments-qualitative-feedback", "info", "en_US", "WooCommerce Payments setup - let us know what you think", "Congrats on enabling WooCommerce Payments for your store. Please share your feedback in this 2 minute survey to help us improve the setup process.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("10", "share-your-feedback-on-paypal", "info", "en_US", "Share your feedback on PayPal", "Share your feedback in this 2 minute survey about how we can make the process of accepting payments more useful for your store.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("11", "google_listings_and_ads_install", "marketing", "en_US", "Drive traffic and sales with Google", "Reach online shoppers to drive traffic and sales for your store by showcasing products across Google, for free or with ads.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("12", "wc-subscriptions-security-update-3-0-15", "info", "en_US", "WooCommerce Subscriptions security update!", "We recently released an important security update to WooCommerce Subscriptions. To ensure your site’s data is protected, please upgrade <strong>WooCommerce Subscriptions to version 3.0.15</strong> or later.<br /><br />Click the button below to view and update to the latest Subscriptions version, or log in to <a href=\"https://woocommerce.com/my-dashboard\">WooCommerce.com Dashboard</a> and navigate to your <strong>Downloads</strong> page.<br /><br />We recommend always using the latest version of WooCommerce Subscriptions, and other software running on your site, to ensure maximum security.<br /><br />If you have any questions we are here to help — just <a href=\"https://woocommerce.com/my-account/create-a-ticket/\">open a ticket</a>.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("13", "woocommerce-core-update-5-4-0", "info", "en_US", "Update to WooCommerce 5.4.1 now", "WooCommerce 5.4.1 addresses a checkout issue discovered in WooCommerce 5.4. We recommend upgrading to WooCommerce 5.4.1 as soon as possible.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("14", "wcpay-promo-2020-11", "marketing", "en_US", "wcpay-promo-2020-11", "wcpay-promo-2020-11", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("15", "wcpay-promo-2020-12", "marketing", "en_US", "wcpay-promo-2020-12", "wcpay-promo-2020-12", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("16", "ppxo-pps-upgrade-paypal-payments-1", "info", "en_US", "Get the latest PayPal extension for WooCommerce", "Heads up! There’s a new PayPal on the block!<br /><br />Now is a great time to upgrade to our latest <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">PayPal extension</a> to continue to receive support and updates with PayPal.<br /><br />Get access to a full suite of PayPal payment methods, extensive currency and country coverage, and pay later options with the all-new PayPal extension for WooCommerce.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("17", "ppxo-pps-upgrade-paypal-payments-2", "info", "en_US", "Upgrade your PayPal experience!", "Get access to a full suite of PayPal payment methods, extensive currency and country coverage, offer subscription and recurring payments, and the new PayPal pay later options.<br /><br />Start using our <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">latest PayPal today</a> to continue to receive support and updates.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("18", "woocommerce-core-sqli-july-2021-need-to-update", "update", "en_US", "Action required: Critical vulnerabilities in WooCommerce", "In response to a critical vulnerability identified on July 13, 2021, we are working with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br />Our investigation into this vulnerability is ongoing, but <strong>we wanted to let you know now about the importance of updating immediately</strong>.<br /><br />For more information on which actions you should take, as well as answers to FAQs, please urgently review our blog post detailing this issue.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("19", "woocommerce-blocks-sqli-july-2021-need-to-update", "update", "en_US", "Action required: Critical vulnerabilities in WooCommerce Blocks", "In response to a critical vulnerability identified on July 13, 2021, we are working with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br />Our investigation into this vulnerability is ongoing, but <strong>we wanted to let you know now about the importance of updating immediately</strong>.<br /><br />For more information on which actions you should take, as well as answers to FAQs, please urgently review our blog post detailing this issue.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("20", "woocommerce-core-sqli-july-2021-store-patched", "update", "en_US", "Solved: Critical vulnerabilities patched in WooCommerce", "In response to a critical vulnerability identified on July 13, 2021, we worked with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br /><strong>Your store has been updated to the latest secure version(s)</strong>. For more information and answers to FAQs, please review our blog post detailing this issue.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("21", "woocommerce-blocks-sqli-july-2021-store-patched", "update", "en_US", "Solved: Critical vulnerabilities patched in WooCommerce Blocks", "In response to a critical vulnerability identified on July 13, 2021, we worked with the WordPress Plugins Team to deploy software updates to stores running WooCommerce (versions 3.3 to 5.5) and the WooCommerce Blocks feature plugin (versions 2.5 to 5.5).<br /><br /><strong>Your store has been updated to the latest secure version(s)</strong>. For more information and answers to FAQs, please review our blog post detailing this issue.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("22", "habit-moment-survey", "marketing", "en_US", "We’re all ears! Share your experience so far with WooCommerce", "We’d love your input to shape the future of WooCommerce together. Feel free to share any feedback, ideas or suggestions that you have.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("23", "woocommerce-core-paypal-march-2022-updated", "update", "en_US", "Security auto-update of WooCommerce", "<strong>Your store has been updated to the latest secure version of WooCommerce</strong>. We worked with WordPress to deploy PayPal Standard security updates for stores running WooCommerce (version 3.5 to 6.3). It’s recommended to disable PayPal Standard, and use <a href=\"https://woocommerce.com/products/woocommerce-paypal-payments/\" target=\"_blank\">PayPal Payments</a> to accept PayPal.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("24", "woocommerce-core-paypal-march-2022-updated-nopp", "update", "en_US", "Security auto-update of WooCommerce", "<strong>Your store has been updated to the latest secure version of WooCommerce</strong>. We worked with WordPress to deploy security updates related to PayPal Standard payment gateway for stores running WooCommerce (version 3.5 to 6.3).", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("25", "pinterest_03_2022_update", "marketing", "en_US", "Your Pinterest for WooCommerce plugin is out of date!", "Update to the latest version of Pinterest for WooCommerce to continue using this plugin and keep your store connected with Pinterest. To update, visit <strong>Plugins &gt; Installed Plugins</strong>, and click on “update now” under Pinterest for WooCommerce.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("26", "store_setup_survey_survey_q2_2022", "survey", "en_US", "How is your store setup going?", "Our goal is to make sure you have all the right tools to start setting up your store in the smoothest way possible.\r\nWe’d love to know if we hit our mark and how we can improve. To collect your thoughts, we made a 2-minute survey.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("27", "wc-admin-wisepad3", "marketing", "en_US", "Take your business on the go in Canada with WooCommerce In-Person Payments", "Quickly create new orders, accept payment in person for orders placed online, and automatically sync your inventory – no matter where your business takes you. With WooCommerce In-Person Payments and the WisePad 3 card reader, you can bring the power of your store anywhere.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("28", "woocommerce-payments-august-2022-need-to-update", "update", "en_US", "Action required: Please update WooCommerce Payments", "An updated secure version of WooCommerce Payments is available – please ensure that you’re using the latest patch version. For more information on what action you need to take, please review the article below.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("29", "woocommerce-payments-august-2022-store-patched", "update", "en_US", "WooCommerce Payments has been automatically updated", "You’re now running the latest secure version of WooCommerce Payments. We’ve worked with the WordPress Plugins team to deploy a security update to stores running WooCommerce Payments (version 3.9 to 4.5). For further information, please review the article below.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("30", "google_listings_ads_custom_attribute_mapping_q4_2022", "marketing", "en_US", "Our latest improvement to the Google Listings & Ads extension: Attribute Mapping", "You spoke, we listened. This new feature enables you to easily upload your products, customize your product attributes in one place, and target shoppers with more relevant ads. Extend how far your ad dollars go with each campaign.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("31", "needs-update-eway-payment-gateway-rin-2022-12-20", "update", "en_US", "Security vulnerability patched in WooCommerce Eway Gateway", "In response to a potential vulnerability identified in WooCommerce Eway Gateway versions 3.1.0 to 3.5.0, we’ve worked to deploy security fixes and have released an updated version.\r\nNo external exploits have been detected, but we recommend you update to your latest supported version 3.1.26, 3.2.3, 3.3.1, 3.4.6, or 3.5.1", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("32", "updated-eway-payment-gateway-rin-2022-12-20", "update", "en_US", "WooCommerce Eway Gateway has been automatically updated", "Your store is now running the latest secure version of WooCommerce Eway Gateway. We worked with the WordPress Plugins team to deploy a software update to stores running WooCommerce Eway Gateway (versions 3.1.0 to 3.5.0) in response to a security vulnerability that was discovered.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("33", "ecomm-wc-navigation-survey-2023", "info", "en_US", "Navigating WooCommerce on WordPress.com", "We are improving the WooCommerce navigation on WordPress.com and would love your help to make it better! Please share your experience with us in this 2-minute survey.", "{}", "pending", "woocommerce.com", "2023-03-02 00:35:08", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("34", "wc-admin-choosing-a-theme", "marketing", "en_US", "یک پوسته انتخاب میکنید؟", "پوسته‌های سازگار با ووکامرس را بررسی کنید و یکی را انتخاب کنید که مطابق با نیازهای برند و تجارت شما باشد.", "{}", "unactioned", "woocommerce-admin", "2023-03-03 05:57:11", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("35", "wc-admin-add-first-product-note", "email", "en_US", "اولین محصول خود را اضافه کنید", "{greetings}<br /><br />احسنت؛ شما یک فروشگاه ووکامرس ایجاد کردید! اکنون وقت آن است که اولین محصول خود را اضافه کرده و آماده شروع فروش شوید.<br /><br />There are three ways to add your products: you can <strong>create products manually, import them at once via CSV file</strong>, or <strong>migrate them from another service</strong>.<br /><br /><a href=\"https://woocommerce.com/document/managing-products/?utm_source=help_panel&amp;utm_medium=product\">Explore our docs</a> for more information, or just get started!", "{\"role\":\"administrator\"}", "unactioned", "woocommerce-admin", "2023-03-04 01:45:35", NULL, "0", "plain", "http://localhost:8888/easy/wp-content/plugins/woocommerce/images/admin_notes/dashboard-widget-setup.png", "0", "0", "info");
INSERT INTO `wp_wc_admin_notes` VALUES("36", "wc-admin-mobile-app", "info", "en_US", "نصب نرم افزار موبایل وو", "برنامۀ تلفن همراه ووکامرس را برای مدیریت سفارشات، دریافت اعلان‌های فروش و مشاهدۀ معیارهای کلیدی - هر کجا که هستید، نصب کنید.", "{}", "unactioned", "woocommerce-admin", "2023-03-04 01:45:35", NULL, "0", "plain", "", "0", "0", "info");
